    public static void lispOk(String stringToCheck, boolean weExpect) {
        boolean aLispCheck = lispCheck(stringToCheck);
        if(aLispCheck == weExpect){
            System.out.println("PASSED : " + stringToCheck);
        }else{
            System.out.println("FAILED : " + stringToCheck);
        }
    }


    public static boolean lispCheck(String stringToCheck) {

        StringBuilder inputSB = new StringBuilder(stringToCheck);

        int openIndex = inputSB.indexOf("(");
        int closeIndex = inputSB.indexOf(")");

        while (openIndex != -1 && closeIndex != -1) {
            if (openIndex > closeIndex) {
                break;
            }
            inputSB.deleteCharAt(openIndex);
            inputSB.deleteCharAt(closeIndex - 1);
            openIndex = inputSB.indexOf("(");
            closeIndex = inputSB.indexOf(")");

        }
        return openIndex == -1 && closeIndex == -1;
    }
